mapper.select:
SELECT * FROM ( SELECT A.CAMPNO , A.CAMPNAME , A.CAMPTYPE , CASE WHEN A.CAMPTYPE = 10 THEN '세미나' WHEN A.CAMPTYPE = 20 THEN '뉴스레터' WHEN A.CAMPTYPE = 30 THEN '테스트' END AS CAMPTYPE_ , A.CAMPSTEP , CASE WHEN A.CAMPSTEP = 0 THEN '생성' WHEN A.CAMPSTEP = 1 THEN '추출' WHEN A.CAMPSTEP = 2 THEN '발송' WHEN A.CAMPSTEP = 3 THEN '종료' END AS CAMPSTEP_ , A.STARTDATE,A.ENDDATE , (SELECT USERNAME FROM me800010 WHERE USERNO = A.OWNER) AS OWNER_ , A.CAMPDESC , CONVERT(nvarchar,A.STARTDATE,23) + '-' + CONVERT(nvarchar,A.ENDDATE,23) AS CAMPDATE_
,ROW_NUMBER() OVER(ORDER BY A.CAMPNO DESC) AS ROWNUM ,ISNULL(B.CNT,0) AS CNT ,ISNULL(C.CNT,0) AS CLICKCNT , CONVERT(nvarchar,ISNULL(C.CNT,0)) + ' / ' +CONVERT(nvarchar,ISNULL(B.CNT,0)) AS CLICKTOTAL , CONVERT(nvarchar,ISNULL(D.CNT,0)) + ' / ' +CONVERT(nvarchar,ISNULL(B.CNT,0)) AS SENDTOTAL , CASE WHEN E.SENDFORM = 1 THEN 'EMAIL' WHEN E.SENDFORM = 2 THEN 'SMS' WHEN E.SENDFORM = 3 THEN 'LMS' WHEN E.SENDFORM = 4 THEN 'MMS' END AS CAMPFORM_ FROM cp400010 A LEFT OUTER JOIN (SELECT CAMPNO,COUNT(EMAILLOGID) AS CNT FROM sys980010 GROUP BY CAMPNO) B ON B.CAMPNO = A.CAMPNO LEFT OUTER JOIN (SELECT CAMPNO,COUNT(EMAILLOGID) AS CNT FROM sys980010 WHERE CLICKYN = 0 GROUP BY CAMPNO) C ON C.CAMPNO = A.CAMPNO LEFT OUTER JOIN (SELECT CAMPNO,COUNT(EMAILLOGID) AS CNT FROM sys980010 WHERE RLTCODE = 20 GROUP BY CAMPNO) D ON C.CAMPNO = A.CAMPNO LEFT OUTER JOIN cp400030 E ON E.CAMPNO = A.CAMPNO WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0
AND A.CAMPNAME LIKE '%' + #{campname} + '%'
AND A.CAMPSTEP = #{campstep}
AND A.CAMPTYPE = #{camptype}
AND A.STARTDATE > #{startdate}
AND A.ENDDATE < #{enddate}
AND A.OWNER = #{owner}
AND E.SENDFORM = #{sendform}
) X WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

SELECT count(*)
		FROM cp400010 A
		WHERE A.SITEID = #{siteid}
		AND A.ISDELETE = 0

			AND  A.CAMPNAME LIKE '%' + #{campname} + '%'


			AND  A.CAMPSTEP = #{campstep}


			AND  A.CAMPTYPE = #{camptype}


			AND  A.STARTDATE > #{startdate}


			AND  A.ENDDATE < #{enddate}


			AND  A.OWNER = #{owner}

