mapper.insert:
INSERT INTO cp400021
(
	SITEID
	,CUSTNO
	,CUSTNAME
	,MOBILENO
	,EMAIL
	,CAMPNO
	,REGDATE
	,REGUSER
	,EDTDATE
	,EDTUSER
)
SELECT
	SITEID
	,CUSTNO
	,CUSTNAME 
	,MOBILE1 + MOBILE2 + MOBILE3 AS MOBILE
	,EMAIL
	,#{campno}
	,getDate()
	,#{userno}
	,getDate()
	,#{userno}
FROM cu100010
WHERE SITEID = #{siteid}
AND INFOAGREE = '0'

AND  SEX = #{sex}


AND CUSTGUBUN = #{custgubun}


AND CUSTGRADE = #{custgrade}


AND SUBSTRING(BIRTH,6,2) = #{birth}


AND ACTGRADE = #{actgrade}


AND REGDATE >= #{regdate1}


AND REGDATE <= #{regdate2}


AND Year(getDate())-Year(BIRTH) >= #{age1}


AND Year(getDate())-Year(BIRTH) <= #{age2}

