mapper.select:
SELECT
COUNT(*) 
FROM cu100010  
WHERE
	SITEID = #{siteid}
	AND ISDELETE = 0 

	AND CUSTNAME like '%'+#{custname}+'%'


	AND MOBILE1+MOBILE2+MOBILE3 like '%'+#{mobile}+'%'


	AND EMAIL like '%'+#{email}+'%'


	AND CUSTGUBUN = #{custgubun}


	AND CUSTGRADE = #{custgrade}


	AND REGDATE >= #{fromregdt}


	AND REGDATE <= #{toregdt}


	AND INFOAGREE = #{infoagree}


	AND OWNER = #{owner}


	AND CLINO = #{clino}

SELECT
 *
FROM (
	SELECT 
	@RNUM:=@RNUM + 1 AS ROWNUM
	, CU.CUSTNO, CU.CUSTNAME, CU.DEPTNAME, CU.DUTY
	, CONCAT(CU.MOBILE1,'-',CU.MOBILE2,'-',CU.MOBILE3) AS MOBILE
	, CU.EMAIL, CU.SEX
	, CU.OWNER, B.USERNAME AS OWNER_
	, CU.CUSTGUBUN, CU.CUSTGRADE, CU.INFOAGREE
	, DATE_FORMAT(CU.REGDATE,'%Y-%m-%d') AS REGDATE
	, CU.CLINO, C.CLINAME 
	FROM cu100010 CU  
	LEFT OUTER JOIN me800010 AS B ON CU.OWNER = B.USERNO 
	LEFT OUTER JOIN sa200010 AS C ON CU.CLINO = C.CLINO
	,(SELECT @RNUM := 0) R	
	WHERE
		CU.SITEID = #{siteid}
		AND CU.ISDELETE = 0 

		AND CU.CUSTNAME like CONCAT('%',#{custname},'%')


		AND CONCAT(CU.MOBILE1,CU.MOBILE2,CU.MOBILE3) like CONCAT('%',#{mobile},'%')


		AND CU.EMAIL like CONCAT('%',#{email},'%')


		AND CUSTGUBUN = #{custgubun}


		AND CUSTGRADE = #{custgrade}


		AND CU.REGDATE >= #{fromregdt}


		AND CU.REGDATE <= #{toregdt}


		AND CU.INFOAGREE = #{infoagree}



			AND CU.OWNER = #{owner}


		AND CU.CLINO = #{clino}

	ORDER BY CU.CUSTNAME	
	) T

WHERE ROWNUM between #{startRowNum} and #{endRowNum}

SELECT A.CUSTNO ,A.CUSTNAME, A.DEPTNAME ,A.DUTY ,CONCAT(A.MOBILE1,'-',A.MOBILE2,'-',A.MOBILE3) AS MOBILE ,A.MOBILE1,A.MOBILE2,A.MOBILE3 ,A.EMAIL ,CONCAT(A.WRKTEL1,'-',A.WRKTEL2,'-',A.WRKTEL3) AS WRKTEL ,A.WRKTEL1,A.WRKTEL2,A.WRKTEL3 ,CONCAT(A.WRKFAX1,'-',A.WRKFAX2,'-',A.WRKFAX3) AS WRKFAX ,A.WRKFAX1,A.WRKFAX2,A.WRKFAX3 ,A.WRKURL ,A.WRKADDR1, A.WRKADDR2, A.WRKADDR3 ,A.OWNER, B.USERNAME AS OWNERNAME ,CONCAT(A.HOMTEL1,'-',A.HOMTEL2,'-',A.HOMTEL3) AS HOMTEL ,A.HOMTEL1,A.HOMTEL2,A.HOMTEL3 ,A.SEX ,A.BIRTH ,A.MARRIED ,A.MAILTO ,A.SOLAR ,A.WEDDINGDAY ,A.CUSTGRADE ,A.JOB ,A.HOBBY ,A.CUSTGUBUN ,A.ACTGRADE ,A.HOMADDR1, A.HOMADDR2, A.HOMADDR3 ,CONCAT(A.HOMADDR1 , A.HOMADDR2 , A.HOMADDR3) AS HOMADDR ,A.HOMADDR1, A.HOMADDR2, A.HOMADDR3 ,A.MEMO ,A.INFOAGREE ,C.DENYMAILAD,C.DENYMAILNEWS ,C.DENYMAILSEMINAR ,C.DENYSMSNOMAL,C.DENYSMSSEMINAR,C.DENYSMSAD ,C.DENYDMNEWS ,C.DENYDMSEMINAR,C.DENYDMAD ,C.DENYTELNEWS ,C.DENYTELSEMINAR ,C.DENYFAX ,C.DENYVISIT ,A.CLINO,D.CLINAME ,A.RELCUSTNO , (SELECT A.CUSTNAME FROM CU100010 A INNER JOIN CU100010 SELF ON A.CUSTNO = SELF.RELCUSTNO WHERE SELF.CUSTNO = #{custno}) AS RELCUSTNAME
FROM cu100010 A LEFT OUTER JOIN me800010 B ON A.OWNER = B.USERNO LEFT OUTER JOIN CU100080 C on A.CUSTNO = C.CUSTNO LEFT OUTER JOIN sa200010 D ON A.CLINO = D.CLINO
WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0 AND A.CUSTNO = #{custno}

