mapper.select:
SELECT COUNT(*) FROM cp400021 A INNER JOIN cp400010 B ON A.CAMPNO = B.CAMPNO
WHERE B.ISDELETE = 0 AND B.SITEID = #{siteid} AND A.CUSTNO = #{custno}
AND B.CAMPNAME LIKE CONCAT('%',#{campname},'%')

SELECT * FROM ( SELECT @RNUM:=@RNUM + 1 AS ROWNUM ,B.CAMPNO, B.CAMPNAME ,B.CAMPTYPE, B.CAMPSTEP ,DATE_FORMAT(B.STARTDATE,'%Y%m%d %T') AS STARTDATE ,DATE_FORMAT(B.ENDDATE,'%Y%m%d %T') AS ENDDATE ,B.OWNER ,C.USERNAME
FROM cp400021 A INNER JOIN cp400010 B ON A.CAMPNO = B.CAMPNO LEFT OUTER JOIN me800010 C ON B.OWNER = C.USERNO ,(SELECT @RNUM := 0) R WHERE B.ISDELETE = 0 AND B.SITEID = #{siteid} AND A.CUSTNO = #{custno}
AND B.CAMPNAME LIKE CONCAT('%',#{campname},'%')
ORDER BY B.CAMPNO DESC ) T
WHERE ROWNUM between #{startRowNum} and #{endRowNum}
