mapper.select:
SELECT * FROM ( SELECT A.LICENSENO ,A.LICENSENAME ,A.LICENSECOST ,(SELECT COUNT(*) FROM me810010 WHERE LICENSENO = A.LICENSENO GROUP BY LICENSENO) AS TOTALCNT ,CONVERT(nvarchar,A.REGDATE,23) AS REGDATE ,(SELECT USERNAME FROM me800010 WHERE USERNO = A.REGUSER) AS REGUSER_ ,ROW_NUMBER() OVER(ORDER BY A.LICENSENO DESC) AS ROWNUM FROM ma900020 A WHERE ISDELETE = '0'
AND LICENSENAME LIKE '%'+#{licensename}+'%'
) X WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

mapper.update:
UPDATE ma900020
	SET LICENSENAME = #{licensename}
		,LICENSECOST = #{licensecost}
		,ISDELETE = #{isdelete}
		,EDTUSER = #{edtuser}
		,EDTDATE = getDate()
	WHERE LICENSENO = #{licenseno}

UPDATE ma900020
	SET ISDELETE = '1'
		,EDTUSER = #{edtuser}
		,EDTDATE = getDate()
	WHERE LICENSENO = #{licenseno}

insert.selectKey:
INSERT INTO ma900020
(
LICENSENAME
,LICENSECOST
,ISDELETE
,REGDATE
,REGUSER
,EDTDATE
,EDTUSER
)VALUES(
#{licensename}
,#{licensecost}
,'0'
,getDate()
,#{reguser}
,getDate()
,#{edtuser}
)

SELECT SCOPE_IDENTITY()
