mapper.select:
SELECT FN_GETCODE('servicecode1',servicecode1,#{siteid}) as label , COUNT(servicecode1) as value,SERVICECODE1
	FROM SV300010
	WHERE SITEID = #{siteid}
	AND ISDELETE = 0

		AND RECEPTIONDATE >= #{datefr}


		AND RECEPTIONDATE <= #{dateto}

	GROUP BY servicecode1

SELECT COUNT(SERVICENO) AS CNT
	FROM SV300010
	WHERE SITEID = #{siteid}
	AND ISDELETE = 0

		AND RECEPTIONDATE >= #{datefr}


		AND RECEPTIONDATE <= #{dateto}
