mapper.select:
SELECT * FROM ( SELECT A.USERNO,A.USERNAME,A.USERID ,A.EMAIL,A.USERDUTY ,(SELECT ROLENAME FROM Crud_SaaS.dbo.T_ROLE WHERE A.CHKROLE = ROLENO) AS CHKROLE_ ,CASE WHEN A.ISDELETE = 0 THEN '사용' WHEN A.ISDELETE = 1 THEN '미사용' END AS ISDELETE_ ,CONVERT(nvarchar,A.REGDATE,23) AS REGDATE ,A.MOBILE1,A.MOBILE2,A.MOBILE3 , A.MOBILE1 + A.MOBILE2 + A.MOBILE3 AS MOBILE ,ROW_NUMBER() OVER(ORDER BY A.USERNO DESC) AS ROWNUM FROM Crud_SaaS.dbo.TB980010 A WHERE SITEID = #{siteid} AND ISDELETE = 0
AND USERNAME LIKE '%'+#{username}+'%'
AND MOBILE LIKE '%'+#{mobile}+'%'
AND USERID LIKE '%'+#{userid}+'%'
AND ISDELETE = #{isdelete}
AND REGDATE >=#{strdate}
AND REGDATE <=#{enddate}
) X WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

SELECT count(*)
		FROM Crud_SaaS.dbo.TB980010
		WHERE SITEID = #{siteid}
		AND ISDELETE = 0

			AND USERNAME LIKE '%'+#{username}+'%'


			AND MOBILE LIKE '%'+#{mobile}+'%'


			AND USERID LIKE '%'+#{userid}+'%'


			AND ISDELETE = #{isdelete}


			AND REGDATE >=#{strdate}


			AND REGDATE <=#{enddate}

SELECT count(*)
	FROM Crud_SaaS.dbo.TB980010
	WHERE USERID = #{userid}

