mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY USERNAME) AS ROWNUM ,USERNO, USERNAME, USERID, USERDUTY,CHKAUTH FROM TB980010 WHERE SITEID= #{siteid} AND ISDELETE = 0
AND USERNAME like '%'+#{username}+'%'
) T WHERE ROWNUM BETWEEN #{startRowNum} and #{endRowNum}

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY A.CLINAME) AS ROWNUM ,A.CLINO, A.CLINAME, A.CALLNAME, A.OWNER ,B.USERNAME FROM TB200010 A LEFT OUTER JOIN TB980010 B ON A.OWNER = B.USERNO WHERE A.SITEID= #{siteid} AND A.ISDELETE = 0
AND CLINAME like '%'+#{cliname}+'%'
) T WHERE ROWNUM BETWEEN #{startRowNum} and #{endRowNum}

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY A.CUSTNO) AS ROWNUM ,A.CUSTNO,A.CUSTNAME,A.CLINO,B.CLINAME,A.MOBILE1+A.MOBILE2+A.MOBILE3 AS MOBILE, A.EMAIL FROM CU100010 A LEFT OUTER JOIN TB200010 B ON A.CLINO = B.CLINO WHERE A.SITEID= #{siteid} AND A.ISDELETE = 0
AND A.CUSTNAME like '%'+#{cstname}+'%'
) T WHERE ROWNUM BETWEEN #{startRowNum} and #{endRowNum}
