mapper.select:
SELECT ROWNUM ,SALESCUSTNO ,KEYMAN, ROLENAME, MEMO ,CUSTNO, CUSTNAME, DEPTNAME, DUTY, EMAIL ,SALESNO, SALENAME ,CLINO, CLINAME
FROM ( SELECT (ROW_NUMBER() OVER(ORDER BY B.CUSTNAME)) AS ROWNUM ,A.SALESCUSTNO ,A.KEYMAN, A.ROLENAME, A.MEMO ,B.CUSTNO, B.CUSTNAME, B.DEPTNAME, B.DUTY, B.EMAIL ,C.SALESNO, C.SALENAME ,D.CLINO, D.CLINAME FROM TB250010 A with (nolock) INNER JOIN CU100010 B with (nolock) ON A.CUSTNO = B.CUSTNO INNER JOIN TB210010 C with (nolock) ON A.SALESNO = C.SALESNO LEFT OUTER JOIN TB200010 D with (nolock) ON B.CLINO = D.CLINO
WHERE A.SITEID = #{siteid} AND A.SALESNO = #{salesno} AND A.ISDELETE = 0
AND B.CUSTNAME like '%'+#{custname} +'%'
)t
WHERE ROWNUM between #{startRowNum} and #{endRowNum}

SELECT COUNT(*) FROM TB110010 A INNER JOIN CU100010 B ON A.CUSTNO = B.CUSTNO WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0
AND A.SALESNO = #{salesno}
AND A.CLINO = #{clino}
AND B.CUSTNAME like '%'+ #{contcustname} +'%'
AND B.CUSTNO like '%'+ #{custno} +'%'

SELECT * FROM ( SELECT (ROW_NUMBER() OVER(ORDER BY A.CONTNO DESC)) AS ROWNUM ,A.CONTNO ,A.CTTUSER ,A.CUSTNO ,CONVERT(VARCHAR, ISNULL(A.CTTDATE,''), 112) + ISNULL(A.CTTTIME,'') AS CTTDATE ,A.DURATION ,ISNULL(A.MEMO,'') AS MEMO ,A.CTTLOCATION ,ISNULL(A.CTTCHANNEL,'') AS CTTCHANNEL ,A.CTTTYPE ,A.SALESSTEP ,A.CLINO ,A.SALESNO ,A.CONTGOALID ,A.CTTRESULT ,A.RESPONSECODE ,A.LOGID , B.CUSTNAME, C.USERNAME FROM TB110010 A INNER JOIN CU100010 B ON A.CUSTNO = B.CUSTNO INNER JOIN TB980010 C ON A.CTTUSER = C.USERNO WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0
AND A.SALESNO = #{salesno}
AND A.CLINO = #{clino}
AND B.CUSTNAME like '%'+ #{contcustname} +'%'
AND B.CUSTNO like '%'+ #{custno} +'%'
) T WHERE ROWNUM between #{startRowNum} and #{endRowNum}

