mapper.select:
SELECT
COUNT(*)  	 
FROM TB210020 A	

WHERE
	A.SALESNO = #{salesno}
	AND A.SITEID =#{siteid}

	AND A.STATE = #{state}

SELECT * FROM ( SELECT (ROW_NUMBER() OVER(ORDER BY A.SALESTATENO DESC)) AS ROWNUM ,A.SALESTATENO, A.SITEID, A.SALESNO ,CONVERT(VARCHAR, A.ENTDATE, 112) AS ENTDATE ,CASE WHEN A.STATE = 1 THEN '계약성공종료' WHEN A.STATE = 2 THEN '계약중' WHEN A.STATE = 3 THEN '제안서제출' WHEN A.STATE = 4 THEN '접촉중' WHEN A.STATE = 5 THEN '문의' WHEN A.STATE = 6 THEN '중도포기' WHEN A.STATE = 7 THEN '경쟁실패' ELSE '' END AS STATE ,CASE WHEN A.PRESTATE = 1 THEN '계약성공종료' WHEN A.PRESTATE = 2 THEN '계약중' WHEN A.PRESTATE = 3 THEN '제안서제출' WHEN A.PRESTATE = 4 THEN '접촉중' WHEN A.PRESTATE = 5 THEN '문의' WHEN A.PRESTATE = 6 THEN '중도포기' WHEN A.PRESTATE = 7 THEN '경쟁실패' ELSE '' END AS PRESTATE ,CASE WHEN A.MODREASON = 1 THEN '영업진행' WHEN A.MODREASON = 2 THEN '업무중단' WHEN A.MODREASON = 3 THEN '폐업' WHEN A.MODREASON = 4 THEN '거래처요청' WHEN A.MODREASON = 5 THEN '기타' ELSE '' END AS MODREASON , A.SSDESC , A.PROB ,A.REGUSER, B.USERNAME
FROM TB210020 A LEFT OUTER JOIN TB980010 B ON A.REGUSER = B.USERNO
WHERE A.SALESNO = #{salesno} AND A.SITEID =#{siteid}
AND A.STATE = #{state}
) AS T
WHERE ROWNUM between #{startRowNum} and #{endRowNum}

