mapper.select:
SELECT A.SCHNO, A.SCHNAME ,A.SALESNO, B.SALENAME ,A.CLINO, C.CLINAME ,A.OWNER, D.USERNAME ,A.STARTDATE, A.STARTTIME ,A.ENDDATE, A.ENDTIME ,CONCAT(A.STARTDATE,' ',A.STARTTIME) AS 'START' ,CONCAT(A.ENDDATE,' ',A.ENDTIME) AS 'END' ,A.STARTDATE, A.STARTTIME, A.ENDDATE, A.ENDTIME ,A.PRIVACY, A.COLOR, A.MEMO
FROM TB210080 A LEFT OUTER JOIN TB210010 B ON A.SALESNO = B.SALESNO LEFT OUTER JOIN TB200010 C ON A.CLINO = C.CLINO INNER JOIN TB980010 D ON A.OWNER = D.USERNO
WHERE A.ISDELETE = 0 AND A.SITEID = #{siteid} AND A.SCHNO = #{schno}

