mapper.select:
SELECT 
	COUNT(*)
FROM TB210010  with (nolock)
WHERE 
	SITEID = #{siteid}
	AND ISDELETE = 0

	AND SALENAME LIKE '%'+#{salename}+'%'


	AND OWNER = #{owner}


	AND CLINO = #{clino}


	AND CONVERT(VARCHAR, REGDATE, 112) >= #{fromregdt}


	AND CONVERT(VARCHAR, REGDATE, 112) <= #{toregdt}


	AND SALESTATE = #{salestate}

SELECT ROWNUM, SALESNO, SALENAME ,CLINO, CLINAME, SALESTATE ,CONVERT(VARCHAR, FORDDATE, 23) AS FORDDATE ,CONVERT(VARCHAR, STATEDATE, 23) AS STATEDATE ,OWNER, USERNAME FROM ( SELECT (ROW_NUMBER() OVER(ORDER BY SALESNO DESC)) AS ROWNUM ,A.SALESNO, A.SALENAME ,A.CLINO, B.CLINAME ,A.FORDDATE, A.SALESTATE, A.STATEDATE ,A.OWNER, C.USERNAME FROM TB210010 A with (nolock) LEFT OUTER JOIN TB200010 B with (nolock) ON A.CLINO = B.CLINO LEFT OUTER JOIN TB980010 C with (nolock) ON A.OWNER = C.USERNO
WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0
AND A.SALENAME LIKE '%'+#{salename}+'%'
AND A.OWNER = #{owner}
AND A.CLINO = #{clino}
AND CONVERT(VARCHAR, A.REGDATE, 23) >= #{fromregdt}
AND CONVERT(VARCHAR, A.REGDATE, 23) <= #{toregdt}
AND A.SALESTATE = #{salestate}
) T WHERE ROWNUM between #{startRowNum} and #{endRowNum}

