mapper.select:
SELECT ROWNUM ,SALESCUSTNO ,KEYMAN, ROLENAME, MEMO ,CUSTNO, CUSTNAME, DEPTNAME, DUTY, EMAIL ,SALESNO, SALENAME ,CLINO, CLINAME
FROM ( SELECT (ROW_NUMBER() OVER(ORDER BY B.CUSTNAME)) AS ROWNUM ,A.SALESCUSTNO ,A.KEYMAN, A.ROLENAME, A.MEMO ,B.CUSTNO, B.CUSTNAME, B.DEPTNAME, B.DUTY, B.EMAIL ,C.SALESNO, C.SALENAME ,D.CLINO, D.CLINAME FROM TB250010 A with (nolock) INNER JOIN CU100010 B with (nolock) ON A.CUSTNO = B.CUSTNO INNER JOIN TB210010 C with (nolock) ON A.SALESNO = C.SALESNO LEFT OUTER JOIN TB200010 D with (nolock) ON B.CLINO = D.CLINO
WHERE A.SITEID = #{siteid} AND A.SALESNO = #{salesno} AND A.ISDELETE = 0
AND B.CUSTNAME like '%'+#{custname} +'%'
)t
WHERE ROWNUM between #{startRowNum} and #{endRowNum}

SELECT A.SALESCUSTNO,A.SALESNO ,A.KEYMAN, A.ROLENAME, A.CHKSUPPORT, A.MEMO ,A.CUSTNO, B.CUSTNAME
FROM TB250010 A with (nolock) INNER JOIN CU100010 B with (nolock) ON A.CUSTNO = B.CUSTNO
WHERE A.SITEID = #{siteid} AND A.SALESCUSTNO = #{salescustno}

mapper.update:

