,REGDATE
,REGUSER
,EDTDATE
,EDTUSER
,SERVICETYPE
,SERVICECODE1
,SERVICECODE2
,SERVICECHANNEL
,ISDELETE
,MEMO
,COMPLETE
)VALUES(
#{servicename}
,#{servicedesc}
,#{serviceowner}
,#{receptiondate}
,#{custno}
,#{clino}
,#{servicestep}
,#{owner}
,#{siteid}
,#{filesearchkey}
,NOW()
,#{reguser}
,NOW()
,#{edtuser}
,#{servicetype}
,#{servicecode1}
,#{servicecode2}
,#{servicechannel}
,#{isdelete}
,#{memo}
,#{complete}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE sv300010
	SET SERVICENAME = #{servicename}
	,SERVICEDESC = #{servicedesc}
	,SERVICEOWNER = #{serviceowner}
	,RECEPTIONDATE = #{receptiondate}
	,CUSTNO = #{custno}
	,CLINO = #{clino}
	,OWNER = #{owner}
	,FILESEARCHKEY = #{filesearchkey}
	,EDTDATE = NOW()
	,EDTUSER = #{edtuser}
	,SERVICETYPE = #{servicetype}
	,SERVICECODE1 = #{servicecode1}
	,SERVICECODE2 = #{servicecode2}
	,SERVICECHANNEL = #{servicechannel}
WHERE SERVICENO = #{serviceno}
AND SITEID = #{siteid}

UPDATE sv300010
	SET EDTDATE = NOW()
		,EDTUSER = #{edtuser}
		,ISDELETE = 1
	WHERE SITEID = #{siteid}
	AND SERVICENO = #{serviceno}

UPDATE sv300010
	SET SERVICESTEP = #{servicestep}
WHERE SERVICENO = #{serviceno}
AND SITEID = #{siteid}

Update sv300030
	SET VISITDATE = #{visitdate}
		,VISITTIME = #{visittime}
		,REWARDTYPE = #{rewardtype}
		,CAUSECODE = #{causecode}
		,DELAYTYPE = #{delaytype}
		,DELAYDESC = #{delaydesc}
		,REWARDDESC = #{rewarddesc}
		,FILESEARCHKEY = #{filesearchkey}
		,EDTDATE = NOW()
		,EDTUSER = #{edtuser}
	WHERE SITEID = #{siteid}
	AND SERVICENO = #{serviceno}
	AND REWARDNO = #{rewardno}

UPDATE sv300040
	SET RACTDATE = #{ractdate}
		,SENDYN = #{sendyn}
		,RACTDESC = #{ractdes}
		,FILESEARCHKEY = #{filesearchkey}
		,EDTDATE = NOW()
		,EDTUSER = #{edtuser}
		,RACTCODE = #{ractcode}
WHERE SITEID = #{siteid}
AND SERVICENO = #{serviceno}
AND RACTNO = #{ractno}

