mapper.select:
SELECT * FROM ( SELECT A.SITENAME,A.SITEID,A.PRSDNAME,A.BSNO,A.MOBILE1+'-'+A.MOBILE2+'-'+A.MOBILE3 AS MOBILE ,A.BSTYPE,CONVERT(varchar,A.FREGDATE,23) AS FREGDATE ,CASE WHEN A.SITESIZE = '1' THEN '대기업' WHEN A.SITESIZE = '2' THEN '중견기업' WHEN A.SITESIZE = '3' THEN '중소기업' WHEN A.SITESIZE = '4' THEN '소기업' END AS SITESIZE ,CASE WHEN A.ISDELETE = '0' THEN '사용' WHEN A.ISDELETE = '1' THEN '미사용' END AS ISDELETE ,ROW_NUMBER() OVER(ORDER BY A.SITEID DESC) AS ROWNUM ,(SELECT SUM(BUYCNT) AS BUYCNT FROM Crud_SaaS.dbo.T_SITE_LICENSE WHERE SITEID = A.SITEID) AS BUYCNT FROM Crud_SaaS.dbo.TB980070 A WHERE A.ISDELETE = 0
AND ISNULL(A.FREGDATE,'1900-01-01') > = #{strDate}
AND ISNULL(A.FREGDATE,'1900-01-01') < = #{endDate}
AND A.SITENAME LIKE '%'+#{sitename} +'%'
AND A.SITESIZE = #{sitesize}
) X WHERE ROWNUM Between #{startRowNum} and #{endRowNum}

SELECT 
count(*)
FROM Crud_SaaS.dbo.TB980070
WHERE ISDELETE = 0

	AND ISNULL(FREGDATE,'1900-01-01') > = #{strDate}


	AND ISNULL(FREGDATE,'1900-01-01')  < = #{endDate}


	AND SITENAME LIKE '%'+#{siteName} +'%'


	AND SITESIZE = #{siteSize}

