mapper.select:
		SELECT * FROM (
			SELECT
			A.SERVICENO 
		 	, A.SERVICENAME 
			, (SELECT CODENAME FROM Crud_SaaS.dbo.T_CODE WHERE CODEGRP = 'SERVICECODE' AND SITEID = #{siteid} AND CODEVAL = A.SERVICECODE) AS SERVICECODE_
			, (SELECT CODENAME FROM Crud_SaaS.dbo.T_CODE WHERE CODEGRP = 'SERVICECHANNEL' AND SITEID = #{siteid} AND CODEVAL = A.SERVICECHANNEL) AS SERVICECHANNEL_
			, (SELECT CUSTNAME FROM Crud_SaaS.dbo.CU100010 WHERE CUSTNO = A.CUSTNO) AS CUSTNAME_
			, (SELECT CLINAME FROM Crud_SaaS.dbo.TB200010 WHERE CLINO = A.CLINO) AS CLINAME_
			, CONVERT(nvarchar,A.RECEPTIONDATE, 23) AS RECEPTIONDATE_
			, (SELECT USERNAME FROM Crud_SaaS.dbo.TB980010 WHERE USERNO = A.SERVICEOWNER) AS SERVICEOWNER_
			, (SELECT USERNAME FROM Crud_SaaS.dbo.TB980010 WHERE USERNO = A.OWNER) AS OWNER_
			, (SELECT CODENAME FROM Crud_SaaS.dbo.T_CODE WHERE CODEGRP = 'SERVICESTEP' AND SITEID = #{siteid} AND CODEVAL = A.SERVICESTEP) AS SERVICESTEP_
			, (SELECT CODENAME FROM Crud_SaaS.dbo.T_CODE WHERE CODEGRP = 'SERVICETYPE' AND SITEID = #{siteid} AND CODEVAL = A.SERVICETYPE) AS SERVICETYPE_
			,(ROW_NUMBER() OVER(ORDER BY SERVICENO DESC)) AS ROWNUM
			FROM Crud_SaaS.dbo.T_SERVICE A
			WHERE A.ISDELETE = 0
			AND A.SITEID = #{siteid}

				AND A.RECEPTIONDATE >= #{strDate}


				AND A.RECEPTIONDATE <= #{endDate}


				AND A.CUSTNO = #{custno}


				AND A.SERVICENAME LIKE '%' + #{servicename} + '%'


				AND A.SERVICETYPE = #{servicetype}


				AND A.CLINO = #{clino}


				AND A.SERVICECHANNEL = #{servicechannel}


				AND A.SERVICEOWNER = #{serviceowner}


				AND A.OWNER = #{owner}


				AND A.SERVICESTEP = #{servicestep}

    		 ) X

				WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

SELECT 
	COUNT(*) 
FROM T_SERVICE A
WHERE A.ISDELETE = 0
AND A.SITEID = #{siteid}

		AND A.RECEPTIONDATE >= #{strDate}


		AND A.RECEPTIONDATE <= #{endDate}


		AND A.CUSTNO = #{custno}


		AND A.SERVICENAME = #{servicename}


		AND A.SERVICETYPE = #{servicetype}


		AND A.CLINO = #{clino}


		AND A.SERVICECHANNEL = #{servicechannel}


		AND A.SERVICEOWNER = #{serviceowner}


		AND A.OWNER = #{owner}


		AND A.SERVICESTEP = #{servicestep}

insert.selectKey:
INSERT INTO Crud_SaaS.dbo.T_SERVICE
(
SERVICENAME
,SERVICEDESC
,SERVICEOWNER
,RECEPTIONDATE
,CUSTNO
,CLINO
,SERVICESTEP
,OWNER
,SITEID
,FILESEARCHKEY
,REGDATE
,REGUSER
,EDTDATE
,EDTUSER
,SERVICETYPE
,SERVICECODE
,SERVICECHANNEL
,ISDELETE
)VALUES(
#{servicename}
,#{servicedesc}
,#{serviceowner}
,#{receptiondate}
,#{custno}
,#{clino}
,#{servicestep}
,#{owner}
,#{siteid}
,#{filesearchkey}
,getDate()
,#{reguser}
,getDate()
,#{edtuser}
,#{servicetype}
,#{servicecode}
,#{servicechannel}
,#{isdelete}
)
SELECT SCOPE_IDENTITY()

mapper.update:
UPDATE Crud_SaaS.dbo.T_SERVICE
	SET SERVICENAME = #{servicename}
	,SERVICEDESC = #{servicedesc}
	,SERVICEOWNER = #{serviceowner}
	,RECEPTIONDATE = #{receptiondate}
	,CUSTNO = #{custno}
	,CLINO = #{clino}
	,OWNER = #{owner}
	,FILESEARCHKEY = #{filesearchkey}
	,EDTDATE = getDate()
	,EDTUSER = #{edtuser}
	,SERVICETYPE = #{servicetype}
	,SERVICECODE = #{servicecode}
	,SERVICECHANNEL = #{servicechannel}
WHERE SERVICENO = #{serviceno}
AND SITEID = #{siteid}

UPDATE Crud_SaaS.dbo.T_SERVICE
	SET EDTDATE = getDate()
		,EDTUSER = #{edtuser}
		,ISDELETE = 1
	WHERE SITEID = #{siteid}
	AND SERVICENO = #{serviceno}

UPDATE Crud_SaaS.dbo.T_SERVICE
	SET SERVICESTEP = #{servicestep}
WHERE SERVICENO = #{serviceno}
AND SITEID = #{siteid}

Update Crud_SaaS.dbo.T_REWARD
	SET VISITDATE = #{visitdate}
		,VISITTIME = #{visittime}
		,REWARDTYPE = #{rewardtype}
		,CAUSECODE = #{causecode}
		,DELAYTYPE = #{delaytype}
		,DELAYDESC = #{delaydesc}
		,REWARDDESC = #{rewarddesc}
		,FILESEARCHKEY = #{filesearchkey}
		,EDTDATE = getDate()
		,EDTUSER = #{edtuser}
	WHERE SITEID = #{siteid}
	AND SERVICENO = #{serviceno}
	AND REWARDNO = #{rewardno}

UPDATE Crud_SaaS.dbo.T_RACT
	SET RACTDATE = #{ractdate}
		,SENDYN = #{sendyn}
		,RACTDESC = #{ractdes}
		,FILESEARCHKEY = #{filesearchkey}
		,EDTDATE = getDate()
		,EDTUSER = #{edtuser}
		,RACTCODE = #{ractcode}
WHERE SITEID = #{siteid}
AND SERVICENO = #{serviceno}
AND RACTNO = #{ractno}

mapper.insert:
INSERT T_CONVEY
	(SERVICENO
	,PREVOWNER
	,NEXTOWNER
	,CONVEYREASON
	,CONVEYDATE
	,CONVEYDESC
	,REGDATE
	,REGUSER
	,EDTDATE
	,EDTUSER
	,SITEID
	)VALUES(
	#{serviceno}
	,#{prevowner}
	,#{nextowner}
	,#{conveyreason}
	,#{conveydate}
	,#{conveydesc}
	,getDate()
	,#{reguser}
	,getDate()
	,#{edtuser}
	,#{siteid}
	)

	UPDATE Crud_SaaS.dbo.T_SERVICE
	SET OWNER = #{nextowner}
		,EDTDATE = getDate()
		,EDTUSER = #{edtuser}
	WHERE SERVICENO = #{serviceno}
	AND SITEID = #{siteid}

	UPDATE Crud_SaaS.dbo.T_SERVICE
	SET SERVICESTEP = 3
	WHERE SERVICENO = #{serviceno}
	AND SITEID = #{siteid}
